mapper.select:
SELECT O_NO, O_NUMBER, M_ID, S_ADDRESS, S_ADDRESS2, O_TIME, OD_PERIOD, OD_STATUS , P_NAME, P_NO, MS_NO, P_BRAND, P_NEWIMAGE1, OD_STARTDATE, OD_ENDDATE, OD_AMOUNT, OD_AS, MS_CATEGORY, OD_NO FROM (SELECT ROW_NUMBER()OVER(ORDER BY O_TIME DESC )AS RNUM, M.MS_CATEGORY, O_NO, O.O_NUMBER, M_ID, S_ADDRESS, S_ADDRESS2, O_TIME, OD_PERIOD, OD.OD_STATUS , P_NAME, P.P_NO, MS_NO, P.P_BRAND, P.P_NEWIMAGE1, OD.OD_STARTDATE, OD.OD_ENDDATE, OD.OD_AMOUNT, OD.OD_AS, OD_NO FROM ORDERS O, ORDER_DETAIL OD, PRODUCT P, MESSAGE M WHERE O.O_NUMBER = OD.O_NUMBER AND OD.P_NO = P.P_NO AND M.MS_NOS = O.O_NO AND M.MS_CATEGORY = '주문' AND 1=1
AND O.O_NUMBER=#{search}
AND O.M_ID=#{search}
AND P.P_NAME LIKE '%'||#{search}||'%'
AND OD.OD_STATUS LIKE '%'||#{search}||'%'
AND O_NO=#{search}
ORDER BY O_TIME DESC ) WHERE RNUM BETWEEN #{start} AND #{end}

SELECT NVL(COUNT(*), 0) FROM ORDERS O, ORDER_DETAIL OD, PRODUCT P, MESSAGE M WHERE O.O_NUMBER = OD.O_NUMBER AND OD.P_NO = P.P_NO AND M.MS_NOS = O.O_NO AND M.MS_CATEGORY = '주문' AND 1=1
AND O.O_NUMBER=#{search}
AND O.M_ID=#{search}
AND P.P_NAME LIKE '%'||#{search}||'%'
AND OD_STATUS LIKE '%'||#{search}||'%'
AND O_NO=#{search}

mapper.update:
UPDATE ORDER_DETAIL 
SET OD_STATUS=#{od_status} 
WHERE OD_NO=#{od_no}

UPDATE ORDER_DETAIL
SET OD_STATUS=#{od_status}, OD_STARTDATE=SYSDATE, OD_ENDDATE=SYSDATE + TO_NUMBER(SUBSTR(#{od_period}, 1, 1))
WHERE OD_NO=#{od_no}
