mapper.select:
SELECT CP_NO, CP_CATEGORY, CP_NAME, CP_AMOUNT, CP_LIMITMIN, CP_LIMITMAX, CP_USE
FROM COUPON
WHERE CP_NO=#{cp_no}

SELECT MQR_NO, MQ_NO, MQR_WDATE, MQR_CONTENT
FROM MQ_REPLY
WHERE MQ_NO=#{mq_no}

mapper.update:
UPDATE MYQNA
SET MQ_DEL = 1
WHERE MQ_NO=#{mq_no}

UPDATE MESSAGE
SET MS_STATUS = '처리완료'
WHERE MS_NO=#{ms_no}

UPDATE MYQNA
SET MQ_STATUS = 2, MQ_TYPE = 2
WHERE MQ_NO=#{mq_no}

UPDATE MQ_REPLY
SET MQR_CONTENT=#{mqr_content}
WHERE MQ_NO=#{mq_no}

mapper.select:
SELECT F_NO, F_CATEGORY, F_TITLE, F_CONTENT
FROM FAQ
WHERE F_NO=#{f_no}

mapper.update:
UPDATE FAQ
SET F_CATEGORY=#{f_category}, F_TITLE=#{f_title}, F_CONTENT=#{f_content}
WHERE F_NO=#{f_no}
